mapper.select:
select * from ( select a.ticker as keyCode , a.ticker as code , a.company_name as name , a.cl_pn as pn , a.ratio , a.cnt from ( select ticker, date_, compound, company_name, round(abs(compound), 2) * 100 as ratio, case when compound > 0 then 'P' when compound < 0 then 'N' end cl_pn, count(*) cnt from HDBOWN.prediction_pn_us where 1=1 and date_ = to_char(sysdate, 'YYYYMMDD') group by ticker, date_, compound, company_name ) a where 1=1 and a.cl_pn is not null
and a.cl_pn = #{PN}
order by a.ratio desc ) where rownum < = 10

select * from ( select a.code_, a.name_, nvl(a.close_, 0) as close, nvl(a.diff_, 0) as diff, nvl(a.open_, 0) as open, nvl(a.high_, 0) as high, nvl(a.low_, 0) as low, nvl(a.volume_, 0) as volume, b.ticker_desc1, b.ticker_desc2, b.sise_52_price, b.sise_revenue_rate, b.sise_siga_tot, b.sise_siga_tot2, b.sise_issue_stock_normal, b.toja_discision, b.toja_prop_price, b.toja_eps, b.toja_per, b.toja_comp, b.srim_revenue_rate, b.srim_jibea, b.srim_roa, b.srim_roe, substr(b.srim_value,1,instr(b.srim_value,'.',1,1)-1) as srim_value, b.srim_issue_stock, b.srim_prop_price, b.srim_10_price, b.srim_20_price, a.CONTENT_ from hdbown.prediction_pn a, hdbown.prediction_detail_ko b where 1=1 and a.code_ = b.code_ and replace(a.date_, '-') = b.date_
and a.key_ = #{KEY}
) where rownum = 1

select *
from (
         select  headline
         from    HDBOWN.prediction_pn_us
         where   1=1
           and   date_ = to_char(sysdate, 'YYYYMMDD')
           and   ticker = #{KEY}
     )
