mapper.select:
select * from ( select a.ticker as keyCode , a.ticker as code , a.company_name as name , a.cl_pn as pn , case when a.cl_pn = 'P' then a.ratio_p when a.cl_pn = 'N' then a.ratio_n else 0 end ratio , a.cnt , a.date_ , a.time_ from ( select ticker, date_, pos, neg, company_name, max(time_) as time_, round(abs(pos), 2) * 100 as ratio_p, round(abs(neg), 2) * 100 as ratio_n, case when pos > neg then 'P' when pos < neg then 'N' end cl_pn, count(*) cnt from HDBOWN.prediction_pn_us where 1=1 and date_ = to_char(sysdate+9/24, 'YYYYMMDD') group by ticker, date_, pos, neg, company_name ) a where 1=1 and a.cl_pn is not null
and a.cl_pn = #{PN}
order by ratio desc, a.time_ desc ) where rownum < = 10

select * from ( select a.code_, a.name_, TO_CHAR(nvl(a.close_, 0), 'FM999,999,999,999') as close, TO_CHAR(nvl(a.diff_, 0), 'FM999,999,999,999') as diff, TO_CHAR(nvl(a.open_, 0) , 'FM999,999,999,999') as open, TO_CHAR(nvl(a.high_, 0), 'FM999,999,999,999') as high, TO_CHAR(nvl(a.low_, 0), 'FM999,999,999,999') as low, TO_CHAR(nvl(a.volume_, 0) , 'FM999,999,999,999')as volume, b.ticker_desc1, b.ticker_desc2, b.sise_52_price, b.sise_revenue_rate, b.sise_siga_tot, b.sise_siga_tot2, b.sise_issue_stock_normal, b.toja_discision, b.toja_prop_price, b.toja_eps, b.toja_per, b.toja_comp, b.srim_revenue_rate, b.srim_jibea, b.srim_roa, b.srim_roe, substr(b.srim_value,1,instr(b.srim_value,'.',1,1)-1) as srim_value, b.srim_issue_stock, b.srim_prop_price, b.srim_10_price, b.srim_20_price, a.CONTENT_, nvl((select max(report_) from hdbown.prediction_pn where code_ = a.code_ group by code_), '-') as repoart_ from hdbown.prediction_pn a, hdbown.prediction_detail_ko b where 1=1 and a.code_ = b.code_ and replace(a.date_, '-') = b.date_ and a.date_ = to_char(sysdate+9/24, 'YYYY-MM-DD')
and a.key_ = #{KEY}
) where rownum = 1

select *
from (
         select  a.headline, a.ticker, a.company_name as name,
                 nvl((select max(report_) from hdbown.prediction_pn_us where ticker = a.ticker group by ticker), '-') as repoart_
         from    HDBOWN.prediction_pn_us a
         where   1=1
           and   a.date_ = to_char(sysdate+9/24, 'YYYYMMDD')
           and   a.ticker = #{KEY}
         group by a.ticker, a.company_name, a.headline
     )

when.if:
update  hdbown.prediction_pn_us a
set     feed_p = (select nvl(max(feed_p), 0) + 1 from hdbown.prediction_pn_us where ticker = #{KEY})
where   a.ticker = #{KEY}

update  hdbown.prediction_pn_us a
set     feed_n = (select nvl(max(feed_n), 0) + 1 from hdbown.prediction_pn_us where ticker = #{KEY})
where   a.ticker = #{KEY}

otherwise.if:
update  hdbown.prediction_pn a
set     feed_p_ = (select nvl(max(feed_p_), 0) + 1 from hdbown.prediction_pn where key_ = #{KEY})
where   a.key_ = #{KEY}

update  hdbown.prediction_pn a
set     feed_n_ = (select nvl(max(feed_n_), 0) + 1 from hdbown.prediction_pn where key_ = #{KEY})
where   a.key_ = #{KEY}
